/*
 * toolbar
 */

.toolbar-tagging {
    display: block;
    margin: 0;
    padding: 0;
    position: absolute;
    top: 20%;
    left: 0;
    z-index: 1000;
    .ui-button-text-only {
        .ui-button-text {
            margin: 0;
            padding: 0;
        }
    }
    input {
        display: none;
    }
    .btn-group {
        .btn+.btn {
            margin: 0;
        }
    }
    .btn-group-vertical {
        & > .btn:first-child:not(:last-child) {
            border-radius: 0;
        }
        & > .btn+.btn {
            margin: 0;
            border-radius: 0;
        }
    }
    .btn {
        background-color: @toolbar-btn-bgcolor;
        border: 0;
        border-radius: 0;
        color: @toolbar-btn-color;
        display: block;
        padding: 0;
        margin: 0;
        height: @btn-dimension;
        width: @btn-dimension;
    }
    .btn:focus {
        border: 0;
        border-radius: 0;
        outline: none;
    }
    i {
        line-height: @btn-dimension;
        height: @btn-dimension;
        width: @btn-dimension;
        text-align: center;
    }
    .tb-label {
        background-color: transparent;
        color: white;
        font-size: 1em;
        font-weight: bold;
        height: @btn-dimension;
        line-height: @btn-dimension;
        padding: 0 1em;
        pointer-events: none;
        position: absolute;
        top: 0;
        .toolbar-primary-label-animation-exit;
    }

    /*
     * toolbar: primary
     */

    .toolbar-primary {
        display: block;
        width: @btn-dimension;
        .toolbar-primary-perspective;
        .btn {
            .toolbar-primary-animation-exit;
        }
        .btn:nth-child(2) {
            .toolbar-animation-delay1;
        }
        .btn:nth-child(3) {
            .toolbar-animation-delay2;
        }
        .btn:nth-child(4) {
            .toolbar-animation-delay3;
        }
        .btn:nth-child(5) {
            .toolbar-animation-delay4;
        }
        div.btn { /* toolbar title */
            background-color: #3c3c3c;
            color: #f5f5f5;
        }
        div.btn:focus {
            border: 0;
            border-radius: 0;
            outline: none;
        }
    }
    .toolbar-primary.show {
        .btn {
            opacity: 1;
            .toolbar-primary-animation-enter;
        }
        .btn:hover {
            .tb-label {
                .toolbar-primary-label-animation-enter;
            }
        }
        label.btn:hover {
            transition: @duration1;
        }
    }

    /*
     * toolbar: secondary
     */

    .toolbar-secondary {
        display: block;
        height: @btn-dimension;
        width: (@btn-dimension * 4);
        position: absolute;
        .toolbar-secondary-animation-exit;
        .btn {
            float: left;
        }
    }
    .toolbar-secondary.show {
        .toolbar-secondary-animation-enter;
        label.btn:hover {
            transition: @duration1;
        }
    }
}



.toolbar-tagging .btn-primary:hover,
.toolbar-tagging .btn-primary.active {
    background-color: @toolbar-btn-bgcolor-hover;
    border-color: @toolbar-btn-bgcolor-hover;
    border-radius: 0;
    color: @toolbar-btn-color-hover;
}
